import React, { useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const StoreOwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [passwordData, setPasswordData] = useState({
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [changing, setChanging] = useState(false);

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await axios.get("/api/stores/owner/dashboard");
      setStores(response.data.stores);
    } catch (error) {
      console.error("Error fetching stores:", error);
    } finally {
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? "" : "empty"}`}>
          â˜…
        </span>
      );
    }
    return <div className="rating-stars">{stars}</div>;
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();

    if (!passwordData.oldPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
      toast.error("All fields are required");
      return;
    }

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error("New passwords do not match");
      return;
    }

    setChanging(true);
    try {
      await axios.post("/api/auth/change-password", {
        oldPassword: passwordData.oldPassword,
        newPassword: passwordData.newPassword,
      });
      toast.success("Password changed successfully");
      setShowChangePassword(false);
      setPasswordData({ oldPassword: "", newPassword: "", confirmPassword: "" });
    } catch (err) {
      toast.error(err.response?.data?.msg || "Failed to change password");
    } finally {
      setChanging(false);
    }
  };

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  const totalStores = stores.length;
  const totalRatings = stores.reduce(
    (sum, store) => sum + parseInt(store.total_ratings),
    0
  );
  const averageRating =
    stores.length > 0
      ? (
        stores.reduce(
          (sum, store) => sum + parseFloat(store.average_rating),
          0
        ) / stores.length
      ).toFixed(1)
      : 0;

  return (
    <div>
      <h1 style={{ marginBottom: "30px" }}>Store Owner Dashboard</h1>

      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-number">{totalStores}</div>
          <div className="stat-label">Your Stores</div>
        </div>

        <div className="stat-card">
          <div className="stat-number">{totalRatings}</div>
          <div className="stat-label">Total Ratings</div>
        </div>

        <div className="stat-card">
          <div className="stat-number">{averageRating}</div>
          <div className="stat-label">Average Rating</div>
        </div>
      </div>

      <div className="card">
        <h3>Your Stores</h3>
        {stores.length === 0 ? (
          <p>
            You don't have any stores yet. Contact an administrator to add
            stores to your account.
          </p>
        ) : (
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Store Name</th>
                  <th>Address</th>
                  <th>Average Rating</th>
                  <th>Total Ratings</th>
                  <th>Created</th>
                </tr>
              </thead>
              <tbody>
                {stores.map((store) => (
                  <tr key={store.id}>
                    <td>{store.name}</td>
                    <td>{store.address}</td>
                    <td>
                      {renderStars(Math.round(store.average_rating))}
                      <span style={{ marginLeft: "10px" }}>
                        {parseFloat(store.average_rating).toFixed(1)}/5
                      </span>
                    </td>
                    <td>{store.total_ratings}</td>
                    <td>{new Date(store.created_at).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      <div className="card" style={{ marginTop: "30px" }}>
        <h3>Store Performance Overview</h3>
        {stores.length > 0 && (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
              gap: "20px",
            }}
          >
            {stores.map((store) => (
              <div
                key={store.id}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                  padding: "15px",
                  backgroundColor: "#f8f9fa",
                }}
              >
                <h4 style={{ marginBottom: "10px" }}>{store.name}</h4>
                <p
                  style={{
                    fontSize: "14px",
                    color: "#666",
                    marginBottom: "10px",
                  }}
                >
                  {store.address}
                </p>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <div>
                    <div
                      style={{
                        fontSize: "18px",
                        fontWeight: "bold",
                        color: "#111827",
                      }}
                    >
                      {parseFloat(store.average_rating).toFixed(1)}/5
                    </div>
                    <div style={{ fontSize: "12px", color: "#666" }}>
                      {store.total_ratings} ratings
                    </div>
                  </div>
                  <div>{renderStars(Math.round(store.average_rating))}</div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="card" style={{ marginTop: "30px" }}>
        <h3>Quick Actions</h3>
        <div style={{ display: "flex", gap: "15px", flexWrap: "wrap" }}>
          <button
            className="btn btn-info"
            onClick={() => window.open("/stores", "_blank")}
          >
            View All Stores
          </button>
          <button
            className="btn btn-secondary"
            onClick={() => setShowChangePassword(true)}
          >
            Change Password
          </button>
        </div>
      </div>

      {/* Password Change Modal */}
      {showChangePassword && (
        <div className="modal-backdrop">
          <div className="modal">
            <h3>Change Password</h3>
            <form onSubmit={handlePasswordChange}>
              <input
                type="password"
                name="oldPassword"
                placeholder="Old Password"
                value={passwordData.oldPassword}
                onChange={(e) =>
                  setPasswordData({ ...passwordData, oldPassword: e.target.value })
                }
              />
              <input
                type="password"
                name="newPassword"
                placeholder="New Password"
                value={passwordData.newPassword}
                onChange={(e) =>
                  setPasswordData({ ...passwordData, newPassword: e.target.value })
                }
              />
              <input
                type="password"
                name="confirmPassword"
                placeholder="Confirm New Password"
                value={passwordData.confirmPassword}
                onChange={(e) =>
                  setPasswordData({ ...passwordData, confirmPassword: e.target.value })
                }
              />
              <div style={{ marginTop: "15px", display: "flex", gap: "10px" }}>
                <button type="submit" disabled={changing}>
                  {changing ? "Updating..." : "Update Password"}
                </button>
                <button type="button" onClick={() => setShowChangePassword(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default StoreOwnerDashboard;
