import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalStores: 0,
    totalRatings: 0,
    averageRating: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [usersRes, storesRes, ratingsRes] = await Promise.all([
        axios.get('/api/users'),
        axios.get('/api/stores'),
        axios.get('/api/ratings')
      ]);

      const totalUsers = usersRes.data.total;
      const totalStores = storesRes.data.total;
      const totalRatings = ratingsRes.data.total;

      // Calculate average rating
      const ratings = ratingsRes.data.ratings;
      const averageRating = ratings.length > 0 
        ? (ratings.reduce((sum, r) => sum + r.rating, 0) / ratings.length).toFixed(1)
        : 0;

      setStats({
        totalUsers,
        totalStores,
        totalRatings,
        averageRating
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
      toast.error('Failed to load dashboard statistics');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  return (
    <div>
      <h1 style={{ marginBottom: '30px' }}>Admin Dashboard</h1>

      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-number">{stats.totalUsers}</div>
          <div className="stat-label">Total Users</div>
        </div>

        <div className="stat-card">
          <div className="stat-number">{stats.totalStores}</div>
          <div className="stat-label">Total Stores</div>
        </div>

        <div className="stat-card">
          <div className="stat-number">{stats.totalRatings}</div>
          <div className="stat-label">Total Ratings</div>
        </div>

        <div className="stat-card">
          <div className="stat-number">{stats.averageRating}</div>
          <div className="stat-label">Average Rating</div>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
        <div className="card">
          <h3>User Management</h3>
          <p>Manage users, view profiles, and control access.</p>
          <Link to="/admin/users" className="btn btn-primary">
            Manage Users
          </Link>
        </div>

        <div className="card">
          <h3>Store Management</h3>
          <p>Add, edit, and manage store information.</p>
          <Link to="/admin/stores" className="btn btn-primary">
            Manage Stores
          </Link>
        </div>

        <div className="card">
          <h3>Rating Management</h3>
          <p>View and manage all store ratings and reviews.</p>
          <Link to="/admin/ratings" className="btn btn-primary">
            View Ratings
          </Link>
        </div>

        <div className="card">
          <h3>Store Listings</h3>
          <p>Browse all stores in the system.</p>
          <Link to="/stores" className="btn btn-secondary">
            View Stores
          </Link>
        </div>
      </div>

      <div className="card" style={{ marginTop: '30px' }}>
        <h3>Quick Actions</h3>
        <div style={{ display: 'flex', gap: '15px', flexWrap: 'wrap' }}>
          <button 
            className="btn btn-success"
            onClick={() => window.open('/admin/users', '_blank')}
          >
            Add New User
          </button>
          <button 
            className="btn btn-success"
            onClick={() => window.open('/admin/stores', '_blank')}
          >
            Add New Store
          </button>
          <button 
            className="btn btn-info"
            onClick={() => window.open('/stores', '_blank')}
          >
            Browse Stores
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
