import React, { useState, useEffect } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import RatingModal from "./RatingModal";

const StoreList = () => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("ASC");
  const [selectedStore, setSelectedStore] = useState(null);
  const [showRatingModal, setShowRatingModal] = useState(false);

  useEffect(() => {
    fetchStores();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchTerm, sortBy, sortOrder]);

  const fetchStores = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/stores`);
      let data = response.data.stores || [];

      // Search
      if (searchTerm.trim()) {
        const term = searchTerm.toLowerCase();
        data = data.filter(
          (store) =>
            store.name.toLowerCase().includes(term) ||
            store.address.toLowerCase().includes(term)
        );
      }

      // Sorting
      data.sort((a, b) => {
        if (sortBy === "name") {
          return sortOrder === "ASC"
            ? a.name.localeCompare(b.name)
            : b.name.localeCompare(a.name);
        }
        if (sortBy === "address") {
          return sortOrder === "ASC"
            ? a.address.localeCompare(b.address)
            : b.address.localeCompare(a.address);
        }
        if (sortBy === "created_at") {
          return sortOrder === "ASC"
            ? new Date(a.created_at) - new Date(b.created_at)
            : new Date(b.created_at) - new Date(a.created_at);
        }
        return 0;
      });

      setStores(data);
    } catch (error) {
      console.error("Error fetching stores:", error);
      toast.error("Failed to load stores");
    } finally {
      setLoading(false);
    }
  };

  const handleRateStore = (store) => {
    setSelectedStore(store);
    setShowRatingModal(true);
  };

  const handleRatingSubmit = async (ratingData) => {
    try {
      await axios.post("/api/ratings", {
        store_id: selectedStore.id,
        ...ratingData,
      });

      toast.success("Rating submitted successfully!");
      setShowRatingModal(false);
      setSelectedStore(null);
      fetchStores();
    } catch (error) {
      const message = error.response?.data?.error || "Failed to submit rating";
      toast.error(message);
    }
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span
          key={i}
          style={{
            color: i <= rating ? "#f59e0b" : "#d1d5db",
            fontSize: "18px",
          }}
        >
          ★
        </span>
      );
    }
    return <div className="rating-stars">{stars}</div>;
  };

  if (loading) {
    return (
      <div className="loading" style={{ textAlign: "center", padding: "40px" }}>
        <div style={{ fontSize: "32px", marginBottom: "12px" }}>⏳ Loading...</div>
        <div>Fetching stores, please wait.</div>
      </div>
    );
  }

  return (
    <div>
      <div className="page-header">
        <h1 style={{ fontSize: "28px", marginBottom: "20px" }}>
          🏬 Store Listings
        </h1>
      </div>

      {/* Search */}
      <div className="search-bar" style={{ marginBottom: "16px" }}>
        <input
          type="text"
          placeholder="Search stores by name or address..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="form-control"
          style={{ padding: "8px", width: "100%" }}
        />
      </div>

      {/* Filters */}
      <div className="filters" style={{ display: "flex", gap: "16px", marginBottom: "20px" }}>
        <div className="filter-group">
          <label style={{ marginRight: "8px" }}>Sort by:</label>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="form-control"
          >
            <option value="name">Name</option>
            <option value="address">Address</option>
            <option value="created_at">Created Date</option>
          </select>
        </div>

        <div className="filter-group">
          <label style={{ marginRight: "8px" }}>Order:</label>
          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="form-control"
          >
            <option value="ASC">Ascending</option>
            <option value="DESC">Descending</option>
          </select>
        </div>
      </div>

      {/* Store Table */}
      <div className="card">
        {stores.length === 0 ? (
          <div className="empty-state" style={{ textAlign: "center", padding: "40px" }}>
            <div style={{ fontSize: "48px", marginBottom: "16px" }}>😕</div>
            <h3>No stores found</h3>
            <p>Try adjusting your search criteria or check back later.</p>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="table" style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th>Store Name</th>
                  <th>Address</th>
                  <th>Owner</th>
                  <th>Average Rating</th>
                  <th>Total Ratings</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {stores.map((store) => (
                  <tr key={store.id}>
                    <td>
                      <strong style={{ color: "#111827" }}>{store.name}</strong>
                    </td>
                    <td>{store.address}</td>
                    <td>
                      <span
                        style={{
                          color: store.owner_name ? "#16a34a" : "#6b7280",
                          fontWeight: store.owner_name ? "600" : "400",
                        }}
                      >
                        {store.owner_name || "No owner"}
                      </span>
                    </td>
                    <td>
                      <div style={{ display: "flex", alignItems: "center", gap: "6px" }}>
                        {renderStars(Math.round(store.average_rating || 0))}
                        <span
                          style={{
                            fontWeight: "600",
                            color: "#374151",
                          }}
                        >
                          {parseFloat(store.average_rating || 0).toFixed(1)}/5
                        </span>
                      </div>
                    </td>
                    <td>
                      <span style={{ fontWeight: "600", color: "#6b7280" }}>
                        {store.total_ratings || 0}
                      </span>
                    </td>
                    <td>
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleRateStore(store)}
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: "4px",
                          background: "#2563eb",
                          color: "white",
                          border: "none",
                          borderRadius: "6px",
                          padding: "6px 12px",
                          cursor: "pointer",
                        }}
                      >
                        ⭐ Rate Store
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Rating Modal */}
      {showRatingModal && selectedStore && (
        <RatingModal
          store={selectedStore}
          onClose={() => {
            setShowRatingModal(false);
            setSelectedStore(null);
          }}
          onSubmit={handleRatingSubmit}
        />
      )}
    </div>
  );
};

export default StoreList;
