import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import AdminDashboard from './components/admin/AdminDashboard';
import UserDashboard from './components/user/UserDashboard';
import StoreOwnerDashboard from './components/storeOwner/StoreOwnerDashboard';
import StoreList from './components/stores/StoreList';
import UserManagement from './components/admin/UserManagement';
import StoreManagement from './components/admin/StoreManagement';
import RatingManagement from './components/admin/RatingManagement';
import { Toaster } from 'react-hot-toast';

const App = () => {
  const [demoUserRole, setDemoUserRole] = useState('admin');

  return (
    <div className="App">
      <Navbar />
      <div className="container">
        <div style={{ margin: '20px 0', textAlign: 'center' }}>
          <label>Select Demo Role: </label>
          <select value={demoUserRole} onChange={(e) => setDemoUserRole(e.target.value)}>
            <option value="admin">Admin</option>
            <option value="store_owner">Store Owner</option>
            <option value="user">User</option>
          </select>
        </div>

        <Routes>
          <Route path="/login" element={<Navigate to="/dashboard" />} />
          <Route path="/register" element={<Navigate to="/dashboard" />} />
          <Route path="/dashboard" element={<DashboardRouter userRole={demoUserRole} />} />
          <Route path="/stores" element={<StoreList />} />
          <Route path="/admin/users" element={<UserManagement />} />
          <Route path="/admin/stores" element={<StoreManagement />} />
          <Route path="/admin/ratings" element={<RatingManagement />} />
          <Route path="/" element={<Navigate to="/dashboard" />} />
          <Route path="*" element={<Navigate to="/dashboard" />} />
        </Routes>
      </div>
      <Toaster
        position="top-center"
        reverseOrder={false}
        gutter={8}
        toastOptions={{
          duration: 5000,
          style: { background: '#fff', color: '#363636', boxShadow: '0 4px 12px rgba(0,0,0,0.15)' },
        }}
      />
    </div>
  );
};

const DashboardRouter = ({ userRole }) => {
  switch (userRole) {
    case 'admin':
      return <AdminDashboard />;
    case 'store_owner':
      return <StoreOwnerDashboard />;
    case 'user':
      return <UserDashboard />;
    default:
      return <Navigate to="/dashboard" />;
  }
};

export default App;




