import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import RatingModal from './RatingModal';

const StoreList = () => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('ASC');
  const [selectedStore, setSelectedStore] = useState(null);
  const [showRatingModal, setShowRatingModal] = useState(false);

  useEffect(() => {
    fetchStores();
  }, [searchTerm, sortBy, sortOrder]);

  const fetchStores = async () => {
    try {
      const params = new URLSearchParams({
        search: searchTerm,
        sortBy,
        sortOrder
      });

      const response = await axios.get(`/api/stores?${params}`);
      setStores(response.data.stores);
    } catch (error) {
      console.error('Error fetching stores:', error);
      toast.error('Failed to load stores');
    } finally {
      setLoading(false);
    }
  };

  const handleRateStore = (store) => {
    setSelectedStore(store);
    setShowRatingModal(true);
  };

  const handleRatingSubmit = async (ratingData) => {
    try {
      await axios.post('/api/ratings', {
        store_id: selectedStore.id,
        ...ratingData
      });

      toast.success('Rating submitted successfully!');
      setShowRatingModal(false);
      setSelectedStore(null);
      fetchStores(); // Refresh to show updated ratings
    } catch (error) {
      const message = error.response?.data?.error || 'Failed to submit rating';
      toast.error(message);
    }
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? '' : 'empty'}`}>
          â˜…
        </span>
      );
    }
    return <div className="rating-stars">{stars}</div>;
  };

  if (loading) {
    return <div>Loading stores...</div>;
  }

  return (
    <div>
      <h1 style={{ marginBottom: '30px' }}>Store Listings</h1>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search stores by name or address..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="form-control"
        />
      </div>

      <div className="filters">
        <div className="filter-group">
          <label>Sort by:</label>
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)}
            className="form-control"
          >
            <option value="name">Name</option>
            <option value="address">Address</option>
            <option value="average_rating">Average Rating</option>
            <option value="created_at">Created Date</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Order:</label>
          <select 
            value={sortOrder} 
            onChange={(e) => setSortOrder(e.target.value)}
            className="form-control"
          >
            <option value="ASC">Ascending</option>
            <option value="DESC">Descending</option>
          </select>
        </div>
      </div>

      <div className="card">
        {stores.length === 0 ? (
          <p>No stores found. Try adjusting your search criteria.</p>
        ) : (
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Store Name</th>
                  <th>Address</th>
                  <th>Owner</th>
                  <th>Average Rating</th>
                  <th>Total Ratings</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {stores.map((store) => (
                  <tr key={store.id}>
                    <td>
                      <strong>{store.name}</strong>
                    </td>
                    <td>{store.address}</td>
                    <td>{store.owner_name || 'No owner'}</td>
                    <td>
                      {renderStars(Math.round(store.average_rating))}
                      <span style={{ marginLeft: '10px' }}>
                        {parseFloat(store.average_rating).toFixed(1)}/5
                      </span>
                    </td>
                    <td>{store.total_ratings}</td>
                    <td>
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleRateStore(store)}
                        style={{ marginRight: '10px' }}
                      >
                        Rate Store
                      </button>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => {
                          // This would typically open a detailed view
                          toast.success(`Viewing details for ${store.name}`);
                        }}
                      >
                        View Details
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {showRatingModal && selectedStore && (
        <RatingModal
          store={selectedStore}
          onClose={() => {
            setShowRatingModal(false);
            setSelectedStore(null);
          }}
          onSubmit={handleRatingSubmit}
        />
      )}
    </div>
  );
};

export default StoreList;
